import React from "react";
import { Link, useLocation } from "react-router-dom";

function Footer() {
    const location = useLocation();

    const navItems = [
        { to: "/calendar", icon: "/calendar_icon.png", alt: "Calendar" },
        { to: "/todolist", icon: "/todo_icon.png", alt: "ToDo" },
        { to: "/notes", icon: "/notes_icon.png", alt: "Notes" },
        { to: "/shopping", icon: "/shopping_icon.png", alt: "Shopping List" },
    ];

    return (
        <footer className="bg-[#faf9f8] p-4 shadow-md border-t border-gray-200 fixed bottom-0 left-0 w-full z-50">
            <div className="flex justify-around items-center">
                {navItems.map(({ to, icon, alt }) => (
                    <Link
                        key={to}
                        to={to}
                        className={`hover:opacity-80 ${
                            location.pathname === to ? "opacity-100" : "opacity-60"
                        }`}
                    >
                        <img src={icon} alt={alt} className="h-8 w-8 object-contain" />
                    </Link>
                ))}
            </div>
        </footer>
    );
}

export default Footer;
