import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

function Navbar({ configuredTime }) {
    const [isOpen, setIsOpen] = useState(false);
    const [currentTime, setCurrentTime] = useState(configuredTime || new Date().toLocaleTimeString());
    const [currentDate, setCurrentDate] = useState(new Date().toLocaleDateString());
    const [weather, setWeather] = useState("Loading...");

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentTime(configuredTime || new Date().toLocaleTimeString());
            setCurrentDate(new Date().toLocaleDateString());
        }, 1000);
        return () => clearInterval(interval);
    }, [configuredTime]);

    useEffect(() => {
        const fetchWeather = async () => {
            try {
                const response = await fetch(
                    `https://api.openweathermap.org/data/2.5/weather?q=YOUR_CITY&units=metric&appid=YOUR_API_KEY`
                );
                const data = await response.json();
                setWeather(`${data.main.temp}°C, ${data.weather[0].description}`);
            } catch (error) {
                setWeather("Weather data unavailable");
            }
        };
        fetchWeather();
    }, []);

    const toggleMenu = () => {
        setIsOpen(!isOpen);
    };

    const closeMenu = () => {
        setIsOpen(false);
    };

    return (
        <nav className="bg-[#faf9f8] p-4 shadow-md">
            <div className="flex justify-between items-center">
                <Link to="/dashboard">
                    <img src="/logo.png" alt="Family Planner Logo" className="h-32 w-32 object-contain" />
                </Link>

                <div className="flex items-center gap-4">
                    <div className="text-gray-700 font-semibold text-lg">{currentDate}</div>
                    <div className="text-gray-700 font-semibold text-lg">{weather}</div>
                    <div className="text-gray-700 font-semibold text-lg">{currentTime}</div>

                    <Link to="/settings">
                        <img src="/settings_icon.png" alt="Settings" className="h-8 w-8 object-contain" />
                    </Link>

                    <button
                        className="p-2 rounded focus:outline-none focus:ring-2 focus:ring-gray-600"
                        onClick={toggleMenu}
                    >
                        <img src="/menu_icon.png" alt="Menu" className="h-8 w-8 object-contain" />
                    </button>
                </div>
            </div>

            {isOpen && (
                <ul className="flex flex-col bg-[#faf9f8] p-4 rounded-lg shadow md:shadow-none">
                    <li><Link to="/dashboard" className="block py-2 text-gray-700 hover:text-gray-900" onClick={closeMenu}>Dashboard</Link></li>
                    <li><Link to="/tasklist" className="block py-2 text-gray-700 hover:text-gray-900" onClick={closeMenu}>Task List</Link></li>
                    <li><Link to="/calendar" className="block py-2 text-gray-700 hover:text-gray-900" onClick={closeMenu}>Calendar</Link></li>
                    <li><Link to="/shoppinglist" className="block py-2 text-gray-700 hover:text-gray-900" onClick={closeMenu}>Shopping List</Link></li>
                    <li><Link to="/notes" className="block py-2 text-gray-700 hover:text-gray-900" onClick={closeMenu}>Notes</Link></li>
                    <li><Link to="/todolist" className="block py-2 text-gray-700 hover:text-gray-900" onClick={closeMenu}>ToDo</Link></li>
                </ul>
            )}
        </nav>
    );
}

export default Navbar;
