import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Menu } from "lucide-react";

function Navbar() {
    const [isOpen, setIsOpen] = useState(false);

    return (
        <nav className="bg-blue-600 p-4 text-white shadow-md">
            <div className="flex justify-between items-center">
                {/* Logo and Title */}
                <h1 className="text-xl font-bold flex items-center gap-2">
                    <img src="/logo.png" alt="Family Planner Logo" className="h-10 w-10 object-contain" />
                    Family Planner
                </h1>

                {/* Collapse Menu Icon */}
                <button
                    className="md:hidden p-2 rounded focus:outline-none focus:ring-2 focus:ring-white"
                    onClick={() => setIsOpen(!isOpen)}
                >
                    <Menu size={24} />
                </button>
            </div>

            {/* Navigation Links */}
            <ul className={`md:flex space-x-4 md:space-x-6 mt-4 md:mt-0 ${isOpen ? "block" : "hidden"} md:block`}>
                <li><Link to="/dashboard" className="block py-2 md:py-0">Dashboard</Link></li>
                <li><Link to="/tasklist" className="block py-2 md:py-0">Task List</Link></li>
                <li><Link to="/calendar" className="block py-2 md:py-0">Calendar</Link></li>
                <li><Link to="/shoppinglist" className="block py-2 md:py-0">Shopping List</Link></li>
                <li><Link to="/notes" className="block py-2 md:py-0">Notes</Link></li>
                <li><Link to="/todolist" className="block py-2 md:py-0">ToDo</Link></li>
            </ul>
        </nav>
    );
}

export default Navbar;
